{"path":"%%ENV:SVC_CONF_ENV_BASE_DIR%%/%%ENV:POD%%/init/launcher","mode":755,"uid":"%%ENV:MYSQL_UID%%","gid":"%%ENV:MYSQL_GID%%","fmt":"#!/bin/bash

BASE_DIR=%%ENV:SVC_CONF_ENV_BASE_DIR%%/%%ENV:POD%%
[ -f $BASE_DIR/init/MYSQL_ROOT_PASSWORD ] && . $BASE_DIR/init/MYSQL_ROOT_PASSWORD
SERVER_IP=%%ENV:SERVER_IP%%
SERVER_PORT=%%ENV:SERVER_PORT%%
USER=root

function _status {
        mysqladmin -u $USER -h $SERVER_IP -P $SERVER_PORT -p$MYSQL_ROOT_PASSWORD status
}

function _stop {
        mysqladmin -u $USER -h $SERVER_IP -P $SERVER_PORT -p$MYSQL_ROOT_PASSWORD shutdown
}

function _start {
	cd $BASE_DIR/init
	$BASE_DIR/init/start --defaults-file=$BASE_DIR/etc/mysql/my.cnf
}

case $1 in
start)
        _status && {
                echo "already started"
                exit 0
        }
        _start
        ;;
stop)
        _status || {
                echo "already stopped"
                exit 0
        }
        _stop
        ;;
status)
        _status
        exit $?
        ;;
info)
        ;;
esac

"}

