{"path":"%%ENV:SVC_CONF_ENV_BASE_DIR%%/%%ENV:POD%%/init/launcher","mode":755,"uid":"%%ENV:MYSQL_UID%%","gid":"%%ENV:MYSQL_GID%%","fmt":"#!/bin/bash

BASE_DIR=%%ENV:SVC_CONF_ENV_BASE_DIR%%/%%ENV:POD%%
SERVER_IP=%%ENV:SERVER_IP%%
SERVER_PORT=%%ENV:SVC_CONF_ENV_PORT_ADMIN%%
USER=admin
MAXSCALE_ROOT_PASSWORD=mariadb
function _status {
        maxadmin -u $USER -h $SERVER_IP -P $SERVER_PORT -p$MAXSCALE_ROOT_PASSWORD help
}

function _stop {
        maxadmin -u $USER -h $SERVER_IP -P $SERVER_PORT -p$MAXSCALE_ROOT_PASSWORD shutdown maxscale
}

function _start {
	maxscale -d --config=$BASE_DIR/conf/maxscale.cnf --datadir=$BASE_DIR/data --logdir=$BASE_DIR/data --piddir=$BASE_DIR/data &
}

case $1 in
start)
        _status && {
                echo "already started"
                exit 0
        }
        _start
        ;;
stop)
        _status || {
                echo "already stopped"
                exit 0
        }
        _stop
        ;;
status)
        _status
        exit $?
        ;;
info)
        ;;
esac

"}
